--	Structure:  the install process for the tables posty manages.----	-- bbplug_posty_container:  the directory/tree structure all posts reside in.DROP TABLE IF EXISTS `bbplug_posty_containers`;CREATE TABLE IF NOT EXISTS `bbplug_posty_containers` (  `postyContainerID` int(10) unsigned NOT NULL auto_increment,  `parentID` int(10) unsigned default NULL,  `name` varchar(64) NOT NULL,  `content` text NOT NULL,  `tags` text NOT NULL,  PRIMARY KEY  (`postyContainerID`),  KEY `parent` (`parentID`),  FULLTEXT KEY `tags` (`tags`)) TYPE=MyISAM AUTO_INCREMENT=1 ;-- bbplug_posty_posts:  the actual posts for posty.DROP TABLE IF EXISTS `bbplug_posty_posts`;CREATE TABLE IF NOT EXISTS `bbplug_posty_posts` (  `postyPostID` int(10) unsigned NOT NULL auto_increment,  `parentID` int(10) unsigned NOT NULL default '1',  `userName` varchar(32) NOT NULL,  `dated` timestamp NOT NULL,  `content` text NOT NULL,  PRIMARY KEY  (`postyPostID`),  KEY `parent` (`parentID`)) TYPE=MyISAM AUTO_INCREMENT=1 ;